//Dismiss Bs Alert 
(function (a) {
	a(".alert[data-auto-dismiss]").each(function (d, e) {
		var b = a(e),
			f = b.data("auto-dismiss") || 12000;
		setTimeout(function () {
			b.alert("close")
		}, f)
	})
})(jQuery);


var originalInputSelector = $.fn.validator.Constructor.INPUT_SELECTOR
$.fn.validator.Constructor.INPUT_SELECTOR = originalInputSelector + ', input[data-recaptcha]'

window.verifyRecaptchaCallback = function (response) {
  $('input[data-recaptcha]').val(response).trigger('change')
}

window.expiredRecaptchaCallback = function () {
  $('input[data-recaptcha]').val("").trigger('change')
}

$('form[data-toggle="validator"]')
  .validator({
    custom: {
      recaptcha: function ($el) {
        if (!$el.val()) {
          return "Click the check Box to prove you're not a BOT"
        }
      }
    }
  })


function resizeReCaptcha() {
  var width = $('.g-recaptcha').parent().width();
  if (width < 302) {
      var scale = width / 302;
      $('.g-recaptcha').css('transform', 'scale(' + scale + ')');
      $('.g-recaptcha').css('-webkit-transform', 'scale(' + scale + ')');
      $('.g-recaptcha').css('transform-origin', '0 0');
      $('.g-recaptcha').css('-webkit-transform-origin', '0 0');
  }
};
$(document).ready(function() {
  $(window).on('resize', function(){
    resizeReCaptcha();
  });
  resizeReCaptcha();
});


